const seedDatabase = async () => {
  try {
    await mongoose.connect(process.env.MONGO_URI);
    console.log("‚úÖ Connected to MongoDB");

    // Clear existing data
    await Game.deleteMany({});
    await TriviaQuestion.deleteMany({});
    console.log("üóëÔ∏è Cleared existing games and questions");

    // Insert games
    await Game.insertMany(games);
    console.log(`‚úÖ Inserted ${games.length} games`);

    // Insert trivia questions
    await TriviaQuestion.insertMany(triviaQuestions);
    console.log(`‚úÖ Inserted ${triviaQuestions.length} trivia questions`);

    console.log("üéâ Database seeded successfully!");
    process.exit(0);
  } catch (error) {
    console.error("‚ùå Error seeding database:", error);
    process.exit(1);
  }
};

